import clsx from "clsx";

export default function Button(props) {
    const { children, outline, className, ...rest } = props;

    const classNames = clsx(
        'btn',
        {
            "btn-default": !outline,
            "btn-outline": outline,
        },
        className
    );

    return (
        <button className={classNames} {...rest}>
            {children}
        </button>
    );
}
